/**
 * 用户props.actionName转type
 */
export function propsActionName2State(actionName: string): SpriteType {
  if (actionName === 'rect') {
    return 'rectangle';
  }

  if (actionName === 'text') {
    return 'text';
  }

  if (actionName === 'graffiti') {
    return 'graffiti';
  }

  if (ActionNames.includes(actionName)) {
    return 'template';
  }

  return 'patch_img';
}

/**
 * 用户props.actionName转义
 */
export function normalizeAction(actionName: string) {
  const realActionName = propsActionName2State(actionName);

  return {
    actionName: realActionName,
    actionKey: realActionName === 'patch_img' || realActionName === 'template' ? actionName : ''
  };
}